(function($) {
  $(document).ready(function() {
      // Listen for changes in the select element.
      $('select#your-select-profile-id').change(function() {
          var selectedProfile = $(this).val();

          // Show/hide profile cards based on the selected profile.
          $('.profile-card').hide();
          $('.profile-' + selectedProfile).show();
      });
  });
})(jQuery);
